wp.customize.controlConstructor['inspiro-alignment']=wp.customize.Control.extend({ready:function(){'use strict';var control=this;control.initAlignmentControl()},initAlignmentControl:function(){var control=this;var container=control.container;container.on('click','.inspiro-alignment-buttons button',function(e){e.preventDefault();var $button=jQuery(this);var alignment=$button.data('alignment');container.find('.inspiro-alignment-buttons button').removeClass('active');$button.addClass('active');control.setting.set(alignment)});control.setting.bind(function(value){container.find('.inspiro-alignment-buttons button').removeClass('active');container.find('.inspiro-alignment-buttons button[data-alignment="'+value+'"]').addClass('active')})}}); 